INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28838, 6268, 'Topotecan', 'Diarrhea', 'Topotecan capsules can cause severe and life-threatening diarrhea that may require hospitalization.  It is recommended to withhold treatment for severe diarrhea.  Manage diarrhea caused by topotecan aggressively and do no administer topotecan capsules to patients with Grade 3 or 4 diarrhea.  Reduce the dose of therapy after recovery to Grade 1 or less.  Close monitoring is recommended.', '2', '"Product Information. Hycamtin (topotecan)." SmithKline Beecham  (2001):', 'DDInter', 0);
