INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (28843, 6268, 'Topotecan', 'Lung Diseases, Interstitial', 'Fatal cases of interstitial lung disease (ILD) have occurred with the use of topotecan.  Risk factors include a history of ILD, pulmonary fibrosis, lung cancer, thoracic radiation, and use of pneumotoxic drugs and/or colony stimulating factors.  It is recommended to monitor patients for pulmonary symptoms indicative of ILD and permanently discontinue treatment for confirmed ILD.', '2', '"Product Information. Hycamtin (topotecan)." SmithKline Beecham  (2001):', 'DDInter', 0);
