INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29266, 481, 'Tramadol', 'Depressive Disorder', 'Tramadol should not be prescribed for patients who are suicidal or addiction-prone.  The use of nonnarcotic analgesics should be considered for patients who are suicidal or depressed.  Tramadol should be prescribed with caution for patients with history of misuse and/or are currently taking CNS-active drugs, antidepressant drugs, alcohol in excess, and patients who suffer from emotional disturbance or depression.  Patients should be advised not to exceed the recommended dose and to limit their intake of alcohol.', '2', '"Product Information. Ultram (tramadol)." McNeil Pharmaceutical  (2001):', 'DDInter', 0);
