INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (29371, 74, 'Tretinoin', 'Hyperlipidemias', 'A reversible elevation of cholesterol and/or triglycerides has been reported in up to 60% of patients administered tretinoin.  Venous thrombosis and myocardial infarction have occurred in patients at low risk for such complications.  Therapy with tretinoin should be administered cautiously in patients with or predisposed to hypercholesterolemia and/or hypertriglyceridemia.  Clinical monitoring of lipid profile is recommended.', '2', '"Product Information. Vesanoid (tretinoin)." Roche Laboratories  (2001):', 'DDInter', 0);
