INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30025, 3029, 'Valproic acid', 'HIV Infections', 'Some in vitro studies suggest that valproate stimulates the replication of the HIV and CMV viruses under certain experimental conditions.  The clinical consequences are unknown, and the relevance of these findings is uncertain for patients receiving maximally suppressive antiretroviral therapy.  However, this should be borne in mind when interpreting the results from regular monitoring of the viral load in HIV infected patients receiving valproate or when following CMV infected patients clinically.', '2', '"Product Information. Depakene (valproic acid)." Abbott Pharmaceutical  (2001):|"Product Information. Depakote (divalproex sodium)." Abbott Pharmaceutical  (2001):', 'DDInter', 0);
