INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30266, 2066, 'Verapamil', 'Intestinal Obstruction', 'The controlled-onset, extended-release formulation of verapamil (Covera-HS) contains a non-deformable material.  There have been rare reports of obstructive symptoms in patients with known strictures following the ingestion of similar sustained-release products.  Therapy with the controlled-onset, extended-release formulation of verapamil should be administered cautiously in patients with preexisting severe gastrointestinal narrowing or obstruction, whether pathologic or iatrogenic.', '2', '"Product Information. Covera-HS (verapamil)." Searle  (2001):', 'DDInter', 0);
