INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30283, 2471, 'Varenicline', 'Mental Disorders', 'Serious neuropsychiatric events including, but not limited to, depression, suicidal ideation, suicide attempt, and completed suicide have been reported in patients taking varenicline.  Caution is recommended and patients should be observed for neuropsychiatric symptoms including changes in behavior, hostility, agitation, depressed mood, and suicide-related events, including ideation, behavior, and attempted suicide.', '3', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30284, 16219, 'Varenicline', 'Mental Disorders', 'Serious neuropsychiatric events including, but not limited to, depression, suicidal ideation, suicide attempt, and completed suicide have been reported in patients taking varenicline.  Caution is recommended and patients should be observed for neuropsychiatric symptoms including changes in behavior, hostility, agitation, depressed mood, and suicide-related events, including ideation, behavior, and attempted suicide.', '3', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30285, 2471, 'Varenicline', 'Alcoholism', 'Varenicline can enhance the response to alcohol and patients may experience increased intoxicating effects.  In patients who may use alcohol excessively, it should be kept in mind that the potentiation may increase the danger associated with the use of alcohol.  Advise patients to reduce the amount of alcohol they consume and to use caution driving or operating machinery or engaging in other potentially hazardous activities while taking varenicline until they know how this agent may affect them.', '2', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30286, 16219, 'Varenicline', 'Alcoholism', 'Varenicline can enhance the response to alcohol and patients may experience increased intoxicating effects.  In patients who may use alcohol excessively, it should be kept in mind that the potentiation may increase the danger associated with the use of alcohol.  Advise patients to reduce the amount of alcohol they consume and to use caution driving or operating machinery or engaging in other potentially hazardous activities while taking varenicline until they know how this agent may affect them.', '2', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30287, 2471, 'Varenicline', 'Cardiovascular Diseases', 'There have been reports of cardiovascular events, including ischemic and hemorrhagic events in patients taking varenicline.  Caution is recommended when prescribing this agent.  Patients should be monitored for signs of new or worsening symptoms of cardiovascular disease and appropriate therapy should be instituted according to medical guidelines.', '2', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
