INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (30289, 2471, 'Varenicline', 'Kidney Diseases', 'Varenicline is substantially excreted by the kidney, and the risk of toxic reactions to this drug may be greater in patients with impaired renal function.  Dose reduction is not required in patients with mild to moderate renal impairment.  Dosage adjustment is needed in patients with severe renal impairment, and those with end-stage renal disease undergoing hemodialysis.  Varenicline has been shown to be dialyzed in patients with end-stage renal disease.  Monitoring of renal function is recommended.', '2', '"Product Information. Chantix (varenicline)." Pfizer U.S. Pharmaceuticals Group  (2006):', 'DDInter', 0);
