INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31274, 12309, 'Vincristine', 'Infections', 'Vincristine induces mild, dose-related myelosuppression. The use of vincristine may be contraindicated in patients with known infectious diseases.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with vincristine.  Clinical monitoring of hematopoetic function is recommended.', '2', '"Product Information. Oncovin (vincristine)." Lilly, Eli and Company  (2001):|"Product Information. Marqibo (vinCRIStine liposome)." Talon Therapeutics Inc', 'DDInter', 0);
