INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31313, 29310, 'Vilazodone', 'Glaucoma, Angle-Closure', 'Some antidepressants exert mydriatic activity that can induce increased intraocular pressure and result in angle-closure (narrow-angle) glaucoma in a patient with anatomically narrow angles who does not have a patent iridectomy.  Prior to initiating therapy with these agents, patients should be examined to determine whether they are susceptible to angle closure, and have a prophylactic procedure (e.g., iridectomy), if they are susceptible.  The use of these drugs in patients with untreated anatomically narrow angles should be avoided.', '2', '"Product Information. Serzone (nefazodone)." Bristol-Myers Squibb  (2001):|"Product Information. Viibryd (vilazodone)." Trovis Pharmaceuticals LLC  (2011):|"Product Information. Brintellix (vortioxetine)." Takeda Pharmaceuticals America  (2013):', 'DDInter', 0);
