INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31372, 23315, 'Vinorelbine', 'Bone Marrow Failure Disorders', 'The use of vinorelbine is contraindicated in patients with neutrophil counts < 1000/mm3.  Vinorelbine induces dose-dependent myelosuppression, primarily neutropenia.  Therapy with vinorelbine should be administered with extreme caution and dosage modification considered in patients whose bone marrow reserve may be depressed by prior chemotherapy or irradiation or whose marrow function is recovering from previous cytotoxic therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.   Close clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Navelbine (vinorelbine)." Glaxo Wellcome  (2001):', 'DDInter', 0);
