INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (31626, 7733, 'Warfarin', 'Diabetes Mellitus', 'Therapy with oral anticoagulants should be administered cautiously in patients with severe diabetes because they may be at increased risk for hemorrhage.  The INR should be monitored closely, and patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', '3', '"Product Information. Coumadin (warfarin)." DuPont Pharmaceuticals  (2001):', 'DDInter', 0);
