INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (32920, 69, 'Acitretin', 'Intracranial Hypertension', 'The use of retinoids has been associated with cases of pseudotumor cerebri (benign intracranial hypertension).  Early signs and symptoms include papilledema, headache, nausea and vomiting, and visual disturbances.  Patients who experience symptoms of this disorder while on retinoid therapy should be referred to a neurologist.  If the diagnosis is confirmed, the retinoid should be discontinued permanently.', '3', '"Product Information. Accutane (isotretinoin)." Roche Laboratories  (2001):|"Product Information. Soriatane (acitretin)." Roche Laboratories  (2001):', 'DDInter', 0);
