INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34386, 55, 'Acyclovir', 'Diseases requiring hemodialysis', 'Acyclovir is substantially removed by hemodialysis.  Plasma levels of acyclovir have been shown to reduce by 60% following 6 hours of dialysis.  Acyclovir should be administered after hemodialysis.  Otherwise, an additional dose may be given after each hemodialysis session.', '2', 'Krasny HC, Liao SH, de Miranda P, Laskin OL, Whelton A, Lietman PS "Influence of hemodialysis on acyclovir pharmacokinetics in patients with chronic renal failure." Am J Med 73 (1982):  202-4|"Product Information. Zovirax (acyclovir)." Mylan Pharmaceuticals Inc  (2017):', 'DDInter', 0);
