INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34802, 13907, 'Adalimumab', 'Neurotoxicity Syndromes', 'The use of TNF blocking agents has been associated with rare cases of CNS manifestations of systemic vasculitis, seizure and new onset or exacerbation of clinical symptoms and/or radiographic evidence of central nervous system demyelinating disorders, including multiple sclerosis and optic neuritis, and peripheral demyelinating disorders, including Guillain-Barr syndrome.  Care should be exercised when considering the use of these agents in patients with neurologic disorders and discontinuing the agent is recommended if these disorders develop during therapy.', '3', '"Product Information. Remicade (infliximab)." Centocor Inc  (2001):|"Product Information. Humira (adalimumab)." Abbott Pharmaceutical  (2003):|"Product Information. Cimzia (certolizumab)." UCB Pharma Inc  (2008):|"Product Information. Simponi (golimumab)." Centocor Inc  (2009):', 'DDInter', 0);
