INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (34989, 17544, 'Canagliflozin', 'Hypotension', 'Sodium-glucose cotransporter 2 (SGLT-2) inhibitors cause intravascular volume contraction which may lead to symptomatic hypotension after treatment initiation especially in the elderly, in patients with impaired renal function, patients with low systolic blood pressure, or those using diuretics .  Volume status should be assessed and corrected before starting treatment and patients should be monitored during therapy.', '2', '"Product Information. Invokana (canagliflozin)." Janssen Pharmaceuticals  (2013):|"Product Information. Farxiga (dapagliflozin)." Bristol-Myers Squibb  (2014):|"Product Information. Jardiance (empagliflozin)." Boehringer Ingelheim  (2014):|"Product Information. Steglatro (ertugliflozin)." Merck & Co., Inc  (2017):', 'DDInter', 0);
