INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35053, 15781, 'Capecitabine', 'Bone Marrow Failure Disorders', 'Capecitabine is metabolized by the liver to fluorouracil (5-FU).  Fluorouracil induces bone marrow suppression.  Leukopenia, thrombocytopenia, and anemia, some severe, have been reported with capecitabine therapy.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitoring of hematopoietic function is recommended', '3', '"Product Information. Xeloda (capecitabine)." Roche Laboratories  (2001):', 'DDInter', 0);
