INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35462, 15738, 'Caspofungin', 'Liver Diseases', 'Caspofungin is primarily metabolized by the liver.  In a single-dose study, plasma concentrations of caspofungin after a 70 mg dose in patients with mild hepatic impairment (Child-Pugh score 5 to 6) increased by approximately 55% compared to healthy control subjects, whereas patients with moderate hepatic impairment (Child-Pugh score 7 to 9) had an average plasma increase of 76%.  In a 14-day multiple-dose study (70 mg on the first day followed by 50 mg daily), plasma concentrations in patients with mild hepatic impairment were increased modestly by about 19% to 25% on days 7 and 14 relative to healthy control subjects.  No dosage adjustment is necessary in patients with mild hepatic impairment.  For patients with moderate hepatic impairment, a reduced dosage of 35 mg/day is recommended following the initial 70 mg loading dose.  There is no clinical experience with caspofungin in patients with severe hepatic impairment.', '2', '"Product Information. Cancidas (caspofungin)." Merck & Co., Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35463, 15739, 'Caspofungin', 'Liver Diseases', 'Caspofungin is primarily metabolized by the liver.  In a single-dose study, plasma concentrations of caspofungin after a 70 mg dose in patients with mild hepatic impairment (Child-Pugh score 5 to 6) increased by approximately 55% compared to healthy control subjects, whereas patients with moderate hepatic impairment (Child-Pugh score 7 to 9) had an average plasma increase of 76%.  In a 14-day multiple-dose study (70 mg on the first day followed by 50 mg daily), plasma concentrations in patients with mild hepatic impairment were increased modestly by about 19% to 25% on days 7 and 14 relative to healthy control subjects.  No dosage adjustment is necessary in patients with mild hepatic impairment.  For patients with moderate hepatic impairment, a reduced dosage of 35 mg/day is recommended following the initial 70 mg loading dose.  There is no clinical experience with caspofungin in patients with severe hepatic impairment.', '2', '"Product Information. Cancidas (caspofungin)." Merck & Co., Inc  (2001):', 'DDInter', 0);
