INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (35717, 162, 'Adenosine', 'Sick Sinus Syndrome', 'The use of adenosine is contraindicated in patients with second- or third-degree AV block, sick sinus syndrome, or symptomatic bradycardia in the absence of a functional artificial pacemaker.', '3', 'Reed R, Falk JL, O''Brien J "Untoward reaction to adenosine therapy for supraventricular tachycardia." Am J Emerg Med 9 (1991):  566-70|Engelstein ED, Lerman BB "Adenosine induced intraatrial block." Pacing Clin Electrophysiol 16 (1993):  89-94|"Product Information. Adenocard (adenosine)." Fujisawa  (2001):|Kipel G, Rossi AF, Steinberg LG, Gelb B, Fiumecaldo F, Golinko RJ, Griepp RB "Malignant wide complex tachycardia after adenosine administration to a postoperative pediatric patient with congenital heart disease." Pediatr Cardiol 16 (1995):  36-7', 'DDInter', 0);
