INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (36501, 27980, 'Carbamazepine', 'Porphyrias, Hepatic', 'The use of carbamazepine should be avoided in patients with a history of hepatic porphyria (e.g., acute intermittent porphyria, variegate porphyria, porphyria cutanea tarda).  Acute attacks have been reported in such patients receiving carbamazepine therapy.', '3', '"Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals  (2002):', 'DDInter', 0);
