INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (37128, 51, 'Ceftriaxone', 'Pancreatitis', 'Cases of pancreatitis, possibly secondary to biliary obstruction, have been reported rarely in patients treated with ceftriaxone.  Most patients presented with risk factors for biliary stasis and biliary sludge (preceding major therapy, severe illness, total parenteral nutrition).  A cofactor role of ceftriaxone-related biliary precipitation cannot be ruled out.', '2', '"Product Information. Rocephin (ceftriaxone)." Roche Laboratories  (2002):', 'DDInter', 0);
