INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (37217, 51, 'Ceftriaxone', 'Vitamin K Deficiency', 'Alterations in prothrombin times have rarely occurred in patients treated with ceftriaxone.  Patients with impaired vitamin K synthesis or low vitamin K stores, such a patients with chronic hepatic disease and malnutrition, require monitoring of prothrombin time during treatment.  Vitamin K administration (10 mg per week) might be needed if prothrombin time is prolonged before or during therapy.', '2', '"Product Information. Rocephin (ceftriaxone)." Roche Laboratories  (2002):', 'DDInter', 0);
