INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (37640, 138, 'Cefotaxime', 'Heart Failure', 'Parenteral cefotaxime sodium contains approximately 51 mg (2.2 mEq) of sodium per each gram of cefotaxime activity.  The frozen solutions of cefotaxime sodium are additionally formulated with sodium citrate hydrous as a buffer.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', '"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):', 'DDInter', 0);
