INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38323, 0, 'Cefotetan', 'Liver Diseases', 'Cases of hepatitis have been reported with the use of certain cephalosporins.  Transient rise in AST, ALT, and alkaline phosphatase levels have also been observed.  Caution and monitoring is recommended when these agents are prescribed to patients with hepatic disorders.', '2', '"Product Information. Vantin (cefpodoxime)." Pharmacia and Upjohn|"Product Information. Ceclor (cefaclor)." Lilly, Eli and Company  (2002):|"Product Information. Duricef (cefadroxil)." Bristol-Myers Squibb  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Kefzol (cefazolin)." Lilly, Eli and Company  (2002):|"Product Information. Suprax (cefixime)." Lupin Pharmaceuticals Inc  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Cefzil (cefprozil)." Bristol-Myers Squibb  (2002):|"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Ceftin (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Zinacef (cefuroxime)." Glaxo Wellcome  (2002):|"Product Information. Keflex (cephalexin)." Dista Products Company  (2002):|"Product Information. Cedax (ceftibuten)." Schering-Plough  (2001):|"Product Information. Maxipime (cefepime)." Bristol-Myers Squibb  (2001):|"Product Information. Omnicef (cefdinir)." Parke-Davis  (2001):|"Product Information. Ceclor CD (cefaclor)." Dura Pharmaceuticals  (2001):|"Product Information. Spectracef (cefditoren)." TAP Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38324, 0, 'Cefotetan', 'Epilepsy', 'Cephalosporins have been implicated in triggering seizures.  Nonconvulsive status epilepticus (NCSE), encephalopathy, coma, asterixis, neuromuscular excitability, and myoclonia have been reported with cephalosporins particularly in patients with a history of epilepsy and/or when recommended dosages of cephalosporins were exceeded due to renal impairment.  Adjust dosing based on creatinine clearance.  Anticonvulsant therapy should be continued in patients with known seizure disorders.  If CNS adverse reactions including seizures occur, patients should undergo a neurological evaluation to determine whether treatment should be discontinued.', '2', '"Product Information. Vantin (cefpodoxime)." Pharmacia and Upjohn|"Product Information. Ceclor (cefaclor)." Lilly, Eli and Company  (2002):|"Product Information. Duricef (cefadroxil)." Bristol-Myers Squibb  (2002):|"Product Information. Ancef (cefazolin)." SmithKline Beecham  (2002):|"Product Information. Kefzol (cefazolin)." Lilly, Eli and Company  (2002):|"Product Information. Suprax (cefixime)." Lupin Pharmaceuticals Inc  (2002):|"Product Information. Claforan (cefotaxime)." Hoechst Marion Roussel  (2002):|"Product Information. Cefotan (cefotetan)." Stuart Pharmaceuticals  (2002):|"Product Information. Mefoxin (cefoxitin)." Merck & Co., Inc  (2002):|"Product Information. Cefzil (cefprozil)." Bristol-Myers Squibb  (2002):|"Product Information. Fortaz (ceftazidime)." Glaxo Wellcome  (2002):|"Product Information. Cefizox (ceftizoxime)." Fujisawa  (2002):|"Product Information. Keflex (cephalexin)." Dista Products Company  (2002):|"Product Information. Cedax (ceftibuten)." Schering-Plough  (2001):|"Product Information. Maxipime (cefepime)." Bristol-Myers Squibb  (2001):|"Product Information. Omnicef (cefdinir)." Parke-Davis  (2001):|"Produc