INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38342, 0, 'Cenobamate', 'Liver Diseases', 'Cenobamate should be used with caution in patients with mild to moderate hepatic dysfunction.  Consider a dosage reduction in these patients, with a maximum recommended dosage of 200 mg once a day.  The use in patients with severe hepatic dysfunction is not recommended.', '2', '"Product Information. Xcopri (cenobamate)." SK Life Science, Inc.  (2020):', 'DDInter', 0);
