INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38774, 306, 'Chloroquine', 'Bone Marrow Failure Disorders', 'Adverse hematologic effects including neutropenia, agranulocytosis, aplastic anemia, and thrombocytopenia have been rarely associated with 4-aminoquinolines.  Therapy with 4-aminoquinolines should be administered cautiously in patients with preexisting bone marrow suppression.  A complete blood count should be performed periodically in patients on prolonged therapy.  If any severe blood disorder appears which is not attributable to the disease under treatment, discontinuance of the drug should be considered.', '2', '"Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals  (2002):|Polano MK, Cats A, van Olden GAJ "Agranulocytosis following treatment with hydroxychloroquine sulphate." Lancet 1 (1965):  1275|Winkelman RK, Merwin CF, Brunsting LA "Antimalarial therapy of lupus erythematosus." Ann Intern Med 55 (1961):  772-6|"Product Information. Plaquenil (hydroxychloroquine)." Apothecon Inc  (2022):', 'DDInter', 0);
