INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (38794, 306, 'Chloroquine', 'Liver Diseases', 'Aminoquinolines may concentrate in the liver.  Isolated cases of abnormal liver function and fulminant hepatic failure have been reported.  Therapy with aminoquinolines should be administered cautiously in patients with hepatic disease or alcoholism and in patients receiving other hepatotoxic drugs.  Periodic evaluation of hepatic function should be performed during prolonged therapy.', '2', '"Product Information. Aralen (chloroquine)." Sanofi Winthrop Pharmaceuticals  (2002):|Liu AC "Hepatotoxic reaction to chloroquine phosphate in a patient with previously unrecognized porphyria cutanea tarda." West J Med 162 (1995):  548-51|"Product Information. Plaquenil (hydroxychloroquine)." Apothecon Inc  (2022):|Grossman I, Azaz-Livshits T, Fridlender Z, Muszkat M, Ben-Chetrit E "Mefloquine-induced acute hepatitis." Pharmacotherapy 20 (2000):  1517-9', 'DDInter', 0);
