INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39382, 0, 'Chorionic Gonadotropin (Human)', 'Prostatic Neoplasms', 'The use of chorionic gonadotropin is contraindicated for use in patients with prostatic or other androgen- dependent neoplasm.  HCG activity is identical to that of luteinizing hormone (LH) and, to a small degree, that of follicle-stimulating hormone (FSH), resulting in production of androgens by the testis.', '3', '"Product Information. Pregnyl (chorionic gonadotropin (HCG))." Organon', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39383, 0, 'Chorionic Gonadotropin (Human)', 'Edema', 'Chorionic gonadotropin (HCG) induction of androgen secretion can result in fluid retention.  Therapy with chorionic gonadotropin (HCG) should be administered cautiously in patients adversely affected by sodium and water retention such as patients with cardiac or renal disease, epilepsy, migraine or asthma.', '2', '"Product Information. Pregnyl (chorionic gonadotropin (HCG))." Organon', 'DDInter', 0);
