INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39553, 16649, 'Chlorthalidone', 'Thyroid Diseases', 'Thiazide diuretics may decrease serum PBI (protein-bound iodine) levels without associated thyroid disturbance.  Clinicians should be cognizant of this effect when prescribing or administering thiazide therapy to patients with thyroid disorders.', '2', 'Bech K, Skovsted L, Siersbaek-Nielsen K, Hansen JM "Influence of thiazides on thyroid parameters in man." Acta Endocrinol (Copenh) 89 (1978):  673-8|"Product Information. HydroDIURIL (hydrochlorothiazide)." Merck & Co., Inc  (2002):|"Product Information. Lozol (indapamide)." Rhone Poulenc Rorer  (2002):|"Product Information. Zaroxolyn (metolazone)." Rhone Poulenc Rorer  (2001):|"Product Information. Thalitone (chlorthalidone)." Monarch Pharmaceuticals Inc  (2001):|"Product Information. Diuril (chlorothiazide)." Merck & Co., Inc  (2001):|"Product Information. Enduron (methyclothiazide)." Abbott Pharmaceutical  (2001):|"Product Information. Metahydrin (trichlormethiazide)." Hoechst Marion Roussel  (2001):|"Product Information. Diucardin (hydroflumethiazide)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Renese-R (reserpine-polythiazide)." Pfizer US Pharmaceuticals, New York, NY.', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39554, 358, 'Cholic Acid', 'Liver Failure', 'Cholic acid should be used with caution in patients with liver impairment.  Some patients with liver disease at baseline experienced worsening serum transaminases, elevated bilirubin values, or worsening cholestasis on liver biopsy following treatment.  Liver function should be monitored and treatment with cholic acid should be discontinued in patients who develop worsening of liver function while on treatment.', '2', '"Product Information. Cholbam (cholic acid)." Manchester Pharmaceutical  (2015):', 'DDInter', 0);
