INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (39577, 274, 'Cinacalcet', 'Hypocalcemia', 'Cinacalcet lowers serum calcium, and therefore patients should be carefully monitored for the occurrence of hypocalcemia.  Potential manifestations of hypocalcemia include paresthesias, myalgias, cramping, tetany, and convulsions.  Cinacalcet treatment should not be initiated if serum calcium is less than the lower limit of the normal range (8.4 mg/dL).  Monitor serum calcium 1 week after treatment initiation or dose adjustment, and once every month once the maintenance dose has been established.', '2', '"Product Information. Sensipar (cinacalcet)." Amgen USA  (2004):', 'DDInter', 0);
