INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40415, 2539, 'Chromium picolinate', 'Malabsorption Syndromes', 'The trace metals manganese, chromium, copper, selenium, and zinc are absorbed in the GI tract from dietary sources and following administration of oral supplements.  GI absorption may be decreased in patients with malabsorption syndromes.  Therefore, larger dosages may be required when these supplements are given orally.  Parenteral administration may be appropriate.', '2', '"Product Information. Galzin (zinc acetate)." Teva Pharmaceuticals USA  (2001):|"Product Information. Chroma-Pak (chromic chloride hexahydrate)." Apothecon Inc  (2022):|"Product Information. Sele-Pak (selenium)." Fujisawa|"Product Information. Copper Sulfate (copper sulfate)." Humco Holding Group  (2001):|"Product Information. Manganese Chloride (manganese chloride)." Abbott Pharmaceutical  (2001):|"Product Information. Manganese Sulfate (manganese sulfate)." American Regent Laboratories Inc  (2001):', 'DDInter', 0);
