INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40527, 2416, 'Citalopram', 'Long QT Syndrome', 'Some SSRI antidepressants such as fluoxetine and citalopram have shown to cause QT interval prolongation and ventricular arrhythmias including Torsade de Pointes.  These drugs should be used with caution in patients with congenital QT syndrome, a previous personal or family history of QT prolongation, and ventricular arrhythmia.  Consider periodic EKG assessment on these patients.  Treatment should be discontinued and a cardiac evaluation should be considered if a patient develops signs or symptoms of ventricular arrhythmia.', '2', '"Product Information. Prozac (fluoxetine)." Dista Products Company  (2001):|"Product Information. Celexa (citalopram)." Forest Pharmaceuticals  (2001):', 'DDInter', 0);
