INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40607, 21946, 'Cladribine', 'Nervous System Diseases', 'Severe unspecified neurological toxicity has been reported rarely during cladribine therapy administered at therapeutic doses.  Serious neurological toxicity such as irreversible paraparesis and quadriparesis has been reported in patients receiving four to nine times the recommended dosage of cladribine for hairy cell leukemia.  Therapy with cladribine should be administered cautiously to patients with or predisposed to neurological impairment.', '3', '"Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40608, 30873, 'Cladribine', 'Nervous System Diseases', 'Severe unspecified neurological toxicity has been reported rarely during cladribine therapy administered at therapeutic doses.  Serious neurological toxicity such as irreversible paraparesis and quadriparesis has been reported in patients receiving four to nine times the recommended dosage of cladribine for hairy cell leukemia.  Therapy with cladribine should be administered cautiously to patients with or predisposed to neurological impairment.', '3', '"Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (40609, 21946, 'Cladribine', 'Liver Diseases', 'The pharmacokinetic disposition of cladribine has not be fully assessed. The effect of hepatic impairment on the elimination of cladribine is not known.  Therapy with cladribine should be administered cautiously in patient with existing or predisposition to compromised hepatic function.  Clinical monitoring of hepatic function is recommended.', '2', 'Bryson HM, Sorkin EM "Cladribine. A review of its pharmacodynamic and pharmacokinetic properties and therapeutic potential in haematological malignancies." Drugs 46 (1993):  872-94|"Product Information. Leustatin (cladribine)." Ortho Biotech Inc  (2001):', 'DDInter', 0);
