INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41165, 15942, 'Clonidine', 'Bradycardia', 'Central alpha-2 adrenoreceptor agonists reduce sympathetic outflow from the central nervous system.  Heart rate is decreased, which may lead to or exacerbate sinus bradycardia and atrioventricular block.  Therapy with central alpha-2 adrenoreceptor agonists should be administered cautiously in patients with conduction disturbances such as sinus node dysfunction or AV nodal disease.', '2', 'Byrd BF, Collins HW, Primm RK "Risk factors for severe bradycardia during oral clonidine therapy for hypertension." Arch Intern Med 148 (1988):  729-33|van Zwieten PA, Thoolen MJ, Timmermans PB "The hypotensive activity and side effects of methyldopa, clonidine, and guanfacine." Hypertension 6 (1984):  28-33|Schwartz E, Friedman E, Mouallem M, Farfel Z "Sinus arrest associated with clonidine therapy." Clin Cardiol 11 (1988):  53-4|"Product Information. Tenex (guanfacine)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Wytensin (guanabenz)." Wyeth-Ayerst Laboratories  (2001):|Golusinski LL, Blount BW "Clonidine-induced bradycardia." J Fam Pract 41 (1995):  399-401|"Product Information. Catapres (clonidine)." Boehringer-Ingelheim  (2001):', 'DDInter', 0);
