INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41289, 623, 'Clonazepam', 'Liver Diseases', 'The use of clonazepam is considered by the manufacturer to be contraindicated in patients with clinical or biochemical evidence of significant liver disease.  Clonazepam is primarily metabolized by the liver, and the metabolites are eliminated by the kidney.  Due to the possibility of excess accumulation of metabolites and the unknown effects of such accumulation, therapy with clonazepam should also be administered cautiously in patients with renal impairment.', '3', '"Product Information. Klonopin (clonazepam)." Roche Laboratories  (2001):', 'DDInter', 0);
