INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41472, 2822, 'Clozapine', 'Hepatitis', 'Hepatitis has been reported in association with the use of clozapine, both in patients with and without underlying liver function abnormalities.  Therapy with clozapine should be administered cautiously in patients with preexisting liver disease.  The drug should be discontinued if clinically significant elevations of liver function tests or symptoms of jaundice occur.', '3', 'Grohmann R, Ruther E, Sassim N, Schmidt LG "Adverse effects of clozapine." Psychopharmacology (Berl) 99 (1989):  s101-4|Kellner M, Wiedemann K, Krieg JC, Berg PA "Toxic hepatitis by clozapine treatment." Am J Psychiatry 150 (1993):  985-6|"Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals  (2001):|Marinkovic D, Timotijevic I, Babinski T, Totic S, Paunovic VR "The side-effects of clozapine: a four year follow-up study." Prog Neuropsychopharmacol Biol Psychiatry 18 (1994):  537-44', 'DDInter', 0);
