INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41895, 7483, 'Cyclobenzaprine', 'Anticholinergic Syndrome', 'Cyclobenzaprine has anticholinergic activity, to which elderly patients are particularly sensitive.  Therapy with cyclobenzaprine should be administered cautiously in patients with preexisting conditions that are likely to be exacerbated by anticholinergic activity, such as urinary retention or obstruction; angle-closure glaucoma, untreated intraocular hypertension, or uncontrolled primary open-angle glaucoma; and gastrointestinal obstructive disorders.  Glaucoma should be treated and under control prior to initiation of cyclobenzaprine therapy, and intraocular pressure monitored during therapy.', '2', 'Heckerling PS, Bartow TJ "Paradoxical diaphoresis in cyclobenzaprine poisoning." Ann Intern Med 101 (1984):  881|Linden CH, Mitchiner JC, Lindzon RD, Rumack BH "Cyclobenzaprine overdosage." J Toxicol Clin Toxicol 20 (1983):  281-8|"Product Information. Flexeril (cyclobenzaprine)." Merck & Co., Inc  (2001):', 'DDInter', 0);
