INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (41953, 4177, 'Cyclophosphamide', 'Kidney Diseases', 'In patients with severe renal impairment, decreased renal excretion may result in increased plasma levels of cyclophosphamide and its metabolites, which may result in increased toxicity.  Monitor patients with severe renal impairment (CrCl =10 mL/min to 24 mL/min) for signs and symptoms of toxicity.  Therapy with cyclophosphamide should be administered cautiously in patients with a history of or predisposition to renal dysfunction.  Clinical monitoring of renal function is recommended.', '2', '"Product Information. Cytoxan (cyclophosphamide)." Bristol-Myers Squibb  (2001):', 'DDInter', 0);
