INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42288, 4352, 'Darolutamide', 'Hepatic Insufficiency', 'Patients with moderate hepatic impairment (Child-Pugh Class B) have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild hepatic impairment.  The effect of severe hepatic impairment (Child-Pugh C) on darolutamide pharmacokinetics is unknown.', '2', '"Product Information. Nubeqa (darolutamide)." Bayer HealthCare Pharmaceuticals Inc.  (2019):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42289, 4352, 'Darolutamide', 'Kidney Diseases', 'Patients with severe renal impairment (eGFR 15-29 mL/min/1.73 m2) who are not receiving hemodialysis have a higher exposure to darolutamide and reduction of the dose is recommended (see manufacturer package insert for recommended dose).  No dose reduction is needed for patients with mild or moderate renal impairment.  The effect of end stage renal disease (eGFR <15 mL/min/1.73 m2) on darolutamide pharmacokinetics is unknown.', '2', '"Product Information. Adakveo (crizanlizumab)." Novartis Pharmaceuticals  (2019):', 'DDInter', 0);
