INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42396, 5848, 'Dasatinib', 'Long QT Syndrome', 'Dasatinib has shown to prolong QT interval in a concentration-dependent manner.  Caution should be exercised specially in patients with congenital long QT syndrome, patients with hypokalemia, hypomagnesemia, or with history of arrhythmias and on antiarrhythmic medication.  Hypokalemia or hypomagnesemia should be corrected before starting treatment.  Patients at risk should be monitored with an EKG before treatment and periodically as clinically indicated.  Dose adjustments might be needed.', '2', '"Product Information. Tasigna (nilotinib)." Novartis Pharmaceuticals  (2007):', 'DDInter', 0);
