INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42424, 9026, 'Allopurinol', 'Liver Diseases', 'The use of allopurinol has infrequently been associated with asymptomatic elevations of serum transaminase and alkaline phosphatase levels.  Isolated cases of cholestatic jaundice, hepatic necrosis, granulomatous hepatitis, and hepatomegaly have also been reported.  Therapy with allopurinol should be administered cautiously in patients with liver disease.  Periodic monitoring of liver function tests is recommended in these patients during the early stages of therapy.', '2', 'Al-Kawas FH, Seeff LB, Berendson RA, Zimmerman HJ, Ishak KG "Allopurinol hepatotoxicity." Ann Intern Med 95 (1981):  588-90|Raper R, Ibels L, Lauer C, Barnes P, Lunzer M "Fulminant hepatic failure due to allopurinol." Aust N Z J Med 14 (1984):  63-5|Ohsawa T, Ohtsubo M "Hepatitis associated with allopurinol." Drug Intell Clin Pharm 19 (1985):  431-3|"Product Information. Zyloprim (allopurinol)." Glaxo Wellcome  (2022):', 'DDInter', 0);
