INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42448, 17122, 'Daunorubicin', 'Infections', 'Daunorubicin can induce severe myelosuppression at therapeutic doses.  The use of daunorubicin may be contraindicated in patients with known infectious diseases.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with daunorubicin.  Close clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42449, 17123, 'Daunorubicin', 'Infections', 'Daunorubicin can induce severe myelosuppression at therapeutic doses.  The use of daunorubicin may be contraindicated in patients with known infectious diseases.  All patients should be instructed to immediately report any signs or symptoms suggesting infection such as fever, sore throat, or local infection during therapy with daunorubicin.  Close clinical monitoring of hematopoietic function is recommended.', '3', '"Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42450, 17122, 'Daunorubicin', 'Bone Marrow Failure Disorders', 'Daunorubicin hydrochloride is a potent bone marrow suppressant.  Suppression will occur in all patients given a therapeutic dose of the drug.  Therapy with daunorubicin hydrochloride should not be started in patients with preexisting drug-induced bone marrow suppression unless the benefit from such treatment warrants the risk.  Persistent, severe myelosuppression may result in superinfection or hemorrhage.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitory of hematopoietic function is recommended.', '3', '"Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42451, 17123, 'Daunorubicin', 'Bone Marrow Failure Disorders', 'Daunorubicin hydrochloride is a potent bone marrow suppressant.  Suppression will occur in all patients given a therapeutic dose of the drug.  Therapy with daunorubicin hydrochloride should not be started in patients with preexisting drug-induced bone marrow suppression unless the benefit from such treatment warrants the risk.  Persistent, severe myelosuppression may result in superinfection or hemorrhage.  Patients should be instructed to immediately report any signs or symptoms suggesting bone marrow suppression such as fever, sore throat, local infection, or bleeding.  Close clinical monitory of hematopoietic function is recommended.', '3', '"Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories  (2001):|"Product Information. Daunoxome (daunorubicin liposomal)." Nexstar Pharmaceuticals Inc  (2001):', 'DDInter', 0);
INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42452, 17122, 'Daunorubicin', 'Liver Diseases', 'Daunorubicin is extensively metabolized to a biologically active form within normal erythrocytes and leukocytes.  Further metabolism via reduction and conjugation has been demonstrated.  Approximately 40% of daunorubicin is eliminated in an active form by biliary excretion.  Patients with hepatic impairment may be at increased risk for toxicity. Therapy with daunorubicin should be administered cautiously and the dosage reduced in patients with compromised hepatic function.  Clinical evaluation of liver function prior to each course of treatment with daunorubicin is recommended.', '2', '"Product Information. Cerubidine (daunorubicin)." Wyeth-Ayerst Laboratories  (2001):', 'DDInter', 0);
