INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42501, 21118, 'Deferasirox', 'Hepatic Insufficiency', 'The use of deferasirox should be avoided in patients with severe hepatic impairment.  The starting dose of deferasirox should be reduced by 50% in patients with moderate hepatic impairment.  It is recommended to measure serum transaminases and bilirubin in all patients prior to initiating treatment, every 2 weeks during the first month, and at least monthly or more frequently as clinically appropriate.  Closely monitor patients with mild or moderate hepatic impairment for efficacy and adverse reactions that may require dose titration.', '3', '"Product Information. Exjade (deferasirox)." Novartis Pharmaceuticals  (2005):', 'DDInter', 0);
