INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (42746, 2971, 'Dexmethylphenidate', 'Epilepsy', 'There is some clinical evidence that methylphenidate may lower the seizure threshold, occasionally even in patients without a history of seizures or prior EEG abnormalities.  Therapy with methylphenidate (racemic) or dexmethylphenidate (the more pharmacologically active d-enantiomer) should be administered cautiously in patients with or predisposed to seizures.  The medication should be discontinued if seizures occur during its use.', '3', NULL, 'DDInter', 0);
