INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47357, 13718, 'Alprostadil', 'Hypotension', 'Increased peripheral blood levels of prostaglandin E1 (PGE1) and its metabolites can lead to hypotension and/or dizziness.  Intracavernous injections of alprostadil can lead to increased blood levels of PGE1.  Therapy with alprostadil should be administered cautiously in patients with hypotension.', '2', '"Intracavernous injection of alprostadil for erectile dysfunction." Med Lett Drugs Ther 37 (1995):  83-4|Linet OI, Ogrinc FG "Efficacy and safety of intracavernosal alprostadil in men with erectile dysfunction." N Engl J Med 334 (1996):  873-7|"Product Information. Prostin VR Pediatric (alprostadil)." Pharmacia and Upjohn', 'DDInter', 0);
