INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47365, 13982, 'Dicloxacillin', 'Cystic Fibrosis', 'The penicillinase-resistant penicillins, dicloxacillin and methicillin, are both eliminated by the kidney.  Renal elimination of these penicillins has been shown to increase in patients with cystic fibrosis, resulting in decreased peak serum drug concentrations and AUCs.  Clinicians should be cognizant of these effects when prescribing or administering the antibiotics to patients with cystic fibrosis.', '2', 'Yaffe SJ, Gerbracht LM, Mosovich LL, Mattar ME, Danish M, Jusko WJ "Pharmacokinetics of methicillin in patients with cystic fibrosis." J Infect Dis 135 (1977):  828-31|"Product Information. Dynapen (dicloxacillin)." Apothecon Inc  (2002):|"Product Information. Staphcillin (methicillin)." Apothecon Inc  (2002):', 'DDInter', 0);
