INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47367, 13982, 'Dicloxacillin', 'Heart Failure', 'Each 250 mg capsule of dicloxacillin sodium contains approximately 14 mg (0.6 mEq) of sodium, and each teaspoonful of the 62.5 mg/5 mL oral suspension contains approximately 67 mg (2.9 mEq) of sodium.  The sodium content should be considered in patients with conditions that may require sodium restriction, such as congestive heart failure, hypertension, and fluid retention.', '2', '"Product Information. Dynapen (dicloxacillin)." Apothecon Inc  (2002):', 'DDInter', 0);
