INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47466, 5824, 'Dimethyl fumarate', 'Infections', 'Serious cases of herpes zoster have occurred in patients treated with dimethyl fumarate; patients should be monitored for signs/symptoms of herpes zoster while on dimethyl fumarate.  Other serious opportunistic infections have occurred with dimethyl fumarate, including cases of serious viral, fungal, and bacterial infections.  Patients with signs/symptoms consistent with any of these infections should undergo prompt diagnostic evaluation and receive appropriate treatment.  Withholding dimethyl fumarate treatment in patients with herpes zoster or other serious infections until the infection has resolved should be considered.', '2', '"Product Information. Tecfidera (dimethyl fumarate)." Biogen Idec Inc  (2023):', 'DDInter', 0);
