INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47620, 449, 'Diltiazem', 'Tachycardia, Ventricular', 'The use of intravenous diltiazem or verapamil is contraindicated in patients with ventricular tachycardia.  IV administration of a calcium channel blocker can precipitate cardiac arrest in such patients.  Marked hemodynamic deterioration and ventricular fibrillation have occurred in patients with wide-complex ventricular tachycardia (QRS >= 0.12 seconds) treated with IV verapamil.', '3', 'Buxton AE, Marchlinski FE, Doherty JU, et al. "Hazards of intravenous verapamil for sustained ventricular tachycardia." Am J Cardiol 59 (1987):  1107-10|Winters SL, Schweitzer P, Kupersmith J, Gomes JA "Verapamil-induced polymorphous ventricular tachycardia." J Am Coll Cardiol 6 (1985):  257-9|"Product Information. Calan (verapamil)." Searle  (2001):|Braunwald E, Hauser SL, Kasper DL, Fauci AS, Isselbacher KJ, Longo DL, Martin JB, eds., Wilson JD "Harrison''s Principles of Internal Medicine." New York, NY: McGraw-Hill Health Professionals Division  (1998):', 'DDInter', 0);
