INSERT OR IGNORE INTO disease_interactions (id, med_id, trade_name, disease_name, interaction_text, severity, reference_text, source, created_at) VALUES (47715, 17740, 'Dobutamine', 'Hypotension', 'The use of dobutamine and arbutamine has been infrequently associated with significant hypotension especially in dehydrated patients secondary to beta-2 mediated vasodilation.  Hypovolemia should be corrected before administering dobutamine or arbutamine.  Blood pressure and ECG should be monitored at regular intervals.  Monitoring of cardiac output and pulmonary wedge pressure may also be desired.', '3', 'Rosamond TL, Vacek JL, Hurwitz A, Rowland AJ, Beauchamp GD, Crouse LJ "Hypotension during dobutamine stress echocardiography: initial description and clinical relevance." Am Heart J 123 (1992):  403-7|"Product Information. Dobutrex (dobutamine)." Lilly, Eli and Company  (2002):|Heinle SK, Tice FD, Kisslo J "Hypotension during dobutamine stress echocardiography: is it related to dynamic intraventricular obstruction?" Am Heart J 130 (1995):  314-7|"Product Information. GenESA (arbutamine)." Gensia Inc  (2001):', 'DDInter', 0);
